}

/* Ändere das Aussehen des Buttons beim Klicken (nicht beim Hover) */
#arbeitButton:hover {
    background-color: green; /* Hintergrundfarbe beim Klicken */
    color: white; /* Textfarbe des Buttons beim Klicken */
    border: 1px solid white; /* Rahmen um den Button beim Klicken */
}

