sp_widget.css:
.captcha-images {
  display: flex;
  justify-content: center;
  align-items: center;
}

.captcha-images img {
  width: 150px;
  height: 150px;
  margin: 10px;
  border: 2px solid transparent;
}

.captcha-images img.selected {
  border-color: green;
}

sp_widget.script:
const images = document.querySelectorAll('.captcha-images img');
const verifyButton = document.getElementById('verifyButton');
const verificationResult = document.getElementById('verificationResult');

let selectedImages = [];

images.forEach(image => {
  image.addEventListener('click', () => {
    image.classList.toggle('selected');
    if (image.classList.contains('selected')) {
      selectedImages.push(image);
    } else {
      selectedImages = selectedImages.filter(selected => selected !== image);
    }
  });
});

verifyButton.addEventListener('click', () => {
  if (selectedImages.length >= 2) {
    verificationResult.textContent = 'Verifizierung erfolgreich! Du bist kein Roboter.';
  } else {
    verificationResult.textContent = 'Verifizierung fehlgeschlagen. Bitte wähle mehr Bilder aus.';
  }
});
