sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;


    // Event-Listener, um auf Gebäudedaten zu reagieren
    document.addEventListener('buildingDataUpdated', function (event) {
        var buildingData = event.detail;

        // Rufen Sie hier die Funktion auf, um die Tabelle basierend auf den Gebäudedaten zu erstellen
        createTable(buildingData);
    });

    function createTable(buildingData) {
        // Hier können Sie die Tabelle dynamisch erstellen, basierend auf den übergebenen Gebäudedaten
        // Verwenden Sie buildingData, um die Tabelle mit den relevanten Informationen zu füllen.

        // Beispiel: Erstellen Sie eine einfache Tabelle mit dem Gebäude-Id als Inhalt
        var table = '<table>';
        table += '<tr><th>Gebäude-ID</th></tr>';
        table += '<tr><td>' + buildingData.buildingId + '</td></tr>';
        // Fügen Sie weitere Informationen hinzu...

        table += '</table>';

        // Fügen Sie die erstellte Tabelle in das HTML-Div des Tabellen-Widgets ein
        document.getElementById('tableContainer').innerHTML = table;
    }

};
