sys_script.script:
(function executeRule(current, previous /*optional*/) {
    // Überprüfen, ob das Arbeitsobjekt gelöscht wird
    if (current.operation() === 'delete') {
        // Definieren Sie hier den Namen Ihrer Tabelle "ArbeitHatMitarbeiter"
        var targetTable = 'x_1112263_snuebung_arbeithatmitarbeiter';

        // Definieren Sie den Namen des Felds, das auf die gelöschte Arbeit verweist
        var referenceField = 'arbeitid'; // Ersetzen Sie 'arbeit_referenz' durch den tatsächlichen Feldnamen

        // Definieren Sie die sys_id der gelöschten Arbeit
        var deletedArbeitSysID = current.sys_id; // sys_id des gelöschten Arbeitsobjekts

        // Erstellen Sie eine Query, um Datensätze in "ArbeitHatMitarbeiter" zu finden, die auf die gelöschte Arbeit verweisen
        var gr = new GlideRecord(targetTable);
        //gr.addQuery(referenceField, deletedArbeitSysID);
		gr.addEncodedQuery("arbeitid="+deletedArbeitSysID);
        gr.query();

        // Iterieren Sie über die gefundenen Datensätze und löschen Sie sie
        while (gr.next()) {
            gr.deleteRecord(); // Löschen Sie den Datensatz
        }
    }
})(current, previous);
